async function getLessons(){try{const s=await fetch("data/lessons.json"),e=await s.json();let n='<ul id="lessonSlider" class="lessonSlider">';e.forEach((s=>{n+=`\n        <li class="lesson-card">\n        <img src="${s.image}" alt="${s.title}"> \n        <div class="level">${s.level.join(", ")}</div>\n        <div class="type-duration">\n            <div class="type">${s.type}</div>\n            <div class="duration">${s.timing}</div>\n        </div>\n        <h4 lang="uk">${s.title}</h4>\n        <div class="category">${s.category}</div>\n        <p class="description">${s.description}</p>\n        <div class="subscr-btn">\n            <div class="subscription">${s.plan}</div>\n            <button type="button" class="btn show-btn"onclick="openLesson('${s.lessonUrl}')">${s.buttonText}</button>\n        </div>\n        </li>\n        `})),n+="</ul>",document.getElementById("lessonSlider-wrap").innerHTML=n,$((function(){$("#lessonSlider").lightSlider({item:3,slideMargin:30,itemWidth:370,itemHeight:514,loop:!0,speed:1e3,auto:!0,controls:!1,pause:3e3,pauseOnHover:!0,responsive:[{breakpoint:975,settings:{item:2,slideMove:1,slideMargin:35}},{breakpoint:700,settings:{item:1,slideMove:1,slideMargin:40}}]})}))}catch(s){console.error("Error fetching lessons:",s)}}getLessons();