async function findLessonsByLevel(n){try{const e=await fetch("./data/lessons.json"),s=await e.json();displayFilteredLessons(s.filter((e=>{const s=n.toLowerCase();return e.level.map((n=>n.toLowerCase())).includes(s)})))}catch(n){console.error("Error fetching or filtering lessons:",n)}}function displayFilteredLessons(n){const e=document.getElementById("filteredLessons");e.innerHTML="",n.forEach((n=>{const s=document.createElement("div");s.classList.add("lesson-item"),s.innerHTML=`\n              <div class="lesson-item">\n                  <img src="${n.image}" alt="${n.title}">\n                  <div class="level">${n.level.join(", ")}</div>\n                  <div class="type-duration">\n                      <div class="type">${n.type}</div> \n                      <div class="duration">${n.timing}</div>\n                  </div>\n                  <h4>${n.title}</h4>\n                  <div class="category">${n.category}</div>\n                  <p class="description">${n.description}</p>         \n                  <div class="subscr-btn">\n                      <div class="subscribtion">${n.plan}</div> \n                      <button type="button" class="btn show-btn" onclick="openLesson('${n.lessonUrl}')">${n.buttonText}</button>\n                  </div>\n              </div>\n          `,e.appendChild(s)}))}function handleSearchButtonClick(){findLessonsByLevel(document.getElementById("level").value.toLowerCase())}document.addEventListener("DOMContentLoaded",(function(){document.getElementById("lessons-link");const n=document.getElementById("lessons-grid");fetch("./data/lessons.json").then((n=>n.json())).then((e=>{return s=e,n.innerHTML="",void s.forEach((e=>{const s=document.createElement("div");s.classList.add("lesson-item"),s.innerHTML=`\n\n            <img src="${e.image}" alt="${e.title}">\n            <div class="level"> ${e.level.join(", ")}</div>\n            <div class="type-duration">\n            <div class="type">${e.type}</div> \n            <div class="duration"> ${e.timing}</div>\n            </div>\n            <h4>${e.title}</h4>\n            <div class="category"> ${e.category}</div>\n            <p class="description">${e.description}</p>           \n            <div class="subscr-btn">\n            <div class="subscribtion">${e.plan}</div> \n            <button type="button" class="btn show-btn"onclick="openLesson('${e.lessonUrl}')">${e.buttonText}</button>\n          </div>\n\n            `,n.appendChild(s)}));var s}))}));