async function getNews(){try{const e=await fetch("lessons.json"),i=await e.json();let n='<ul id="newsSlider" class="newsSlider">';i.forEach((e=>{n+=`\n        <li>\n        <div class="news-img">\n        <img src="${e.image}" alt="${e.title}">\n        </div>\n        <h3>${e.title}</h3>\n        <p class="anons">${e.description}</p>\n        <div class="auth-flex">\n           \n        </div>\n      </li>\n        `})),n+="</ul>",document.getElementById("newsSlider-wrap").innerHTML=n;const s=$("#newsSlider").lightSlider({item:3,slideMargin:30,loop:!0,auto:!0,controls:!1,itemWidth:370,itemHeight:514,pause:3e3,pauseOnHover:!0,responsive:[{breakpoint:975,settings:{item:2,slideMove:1,slideMargin:35}},{breakpoint:700,settings:{item:1,slideMove:1,slideMargin:40}}]});$("#newsSliderPrev").on("click",(function(){s.goToPrevSlide()})),$("#newsSliderNext").on("click",(function(){s.goToNextSlide()}))}catch(e){console.error("Error fetching news:",e)}}getNews();